head.script:


function j_date(d){
	var da = d.split(' ');
	day = da[2].replace(/,/,''); // 12
	month = da[1].slice(0,3); // Dec
	document.write('<div class=\'date\'><div class=\'month\'>' +  month + '</div>' + day + '</div>');
}

