function remplaza_fecha(d){
var da = d.split(' ');
dia = "<span class='day'>"+da[0]+"</span>"; 
mes = "<span class='month'>"+da[1].slice(0,3)+"<span class='year'>"+da[2]+"</span>"+"</span>"; 
  document.write(mes+dia);
}

